<?php// Добавляем свойи типы постовfunction register_custom_taxonomy_types(){	$top_release_labels = array(		'name' => 'Releases Categories', // Название во множественном числе		'singular_name' => 'Release Category', // Название в единственном числе		'menu_name' => 'Releases Categories', // Название в меню	);	$top_release_args = array(		'labels' => $top_release_labels,		'public' => true, // Разрешить публичный доступ к таксономии		'hierarchical' => true, // Таксономия иерархическая, как категории		'show_ui' => true, // Показывать интерфейс в административной панели		'show_admin_column' => true, // Показывать колонку в списке записей		'rewrite' => array('slug' => 'release-category'), // Переписать URL таксономии	);	register_taxonomy('release-category', 'releases', $top_release_args);}add_action( 'init', 'register_custom_taxonomy_types' );